mapper.select:

select 

from change_file
where repository_id = #{repositoryId,jdbcType=INTEGER}
  and commit_id = #{commitId,jdbcType=VARCHAR}
  and file_name = #{fileName,jdbcType=VARCHAR}

mapper.delete:

delete from change_file
where repository_id = #{repositoryId,jdbcType=INTEGER}
  and commit_id = #{commitId,jdbcType=VARCHAR}
  and file_name = #{fileName,jdbcType=VARCHAR}

mapper.insert:

insert into change_file (repository_id, commit_id, file_name, 
  change_type, old_path, new_path
  )
values (#{repositoryId,jdbcType=INTEGER}, #{commitId,jdbcType=VARCHAR}, #{fileName,jdbcType=VARCHAR}, 
  #{changeType,jdbcType=VARCHAR}, #{oldPath,jdbcType=VARCHAR}, #{newPath,jdbcType=VARCHAR}
  )

mapper.update:

update change_file

  
    change_type = #{changeType,jdbcType=VARCHAR},
  
  
    old_path = #{oldPath,jdbcType=VARCHAR},
  
  
    new_path = #{newPath,jdbcType=VARCHAR},
  

where repository_id = #{repositoryId,jdbcType=INTEGER}
  and commit_id = #{commitId,jdbcType=VARCHAR}
  and file_name = #{fileName,jdbcType=VARCHAR}


update change_file
set change_type = #{changeType,jdbcType=VARCHAR},
  old_path = #{oldPath,jdbcType=VARCHAR},
  new_path = #{newPath,jdbcType=VARCHAR}
where repository_id = #{repositoryId,jdbcType=INTEGER}
  and commit_id = #{commitId,jdbcType=VARCHAR}
  and file_name = #{fileName,jdbcType=VARCHAR}
