mapper.select:

select

from issue_bug
where repository_id = #{repositoryId,jdbcType=INTEGER}
and issue_id = #{issueId,jdbcType=VARCHAR}

mapper.delete:

delete from issue_bug
where repository_id = #{repositoryId,jdbcType=INTEGER}
and issue_id = #{issueId,jdbcType=VARCHAR}

mapper.insert:

insert into issue_bug (repository_id, issue_id, summary,
assignee, reporter, status,
resolution, created, updated
)
values (#{repositoryId,jdbcType=INTEGER}, #{issueId,jdbcType=VARCHAR},
#{summary,jdbcType=VARCHAR},
#{assignee,jdbcType=VARCHAR}, #{reporter,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR},
#{resolution,jdbcType=VARCHAR}, #{created,jdbcType=TIMESTAMP},
#{updated,jdbcType=TIMESTAMP}
)

mapper.update:

update issue_bug
set summary = #{summary,jdbcType=VARCHAR},
assignee = #{assignee,jdbcType=VARCHAR},
reporter = #{reporter,jdbcType=VARCHAR},
status = #{status,jdbcType=VARCHAR},
resolution = #{resolution,jdbcType=VARCHAR},
created = #{created,jdbcType=TIMESTAMP},
updated = #{updated,jdbcType=TIMESTAMP}
where repository_id = #{repositoryId,jdbcType=INTEGER}
and issue_id = #{issueId,jdbcType=VARCHAR}
